package com.tnsif.loop;

public class FOREACHLOOP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[]= {10,20,30,40,50,60};
		for(int num:arr) {
			System.out.print(num+"\t");
		}
		System.out.println();
		char ch[]= {'j','a','v','a'};
		for(char c:ch) {
			System.out.print(c);
		}
	}

}
