package com.tnsif.loop;

public class NESTEDFORLOOP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i =10;i<=20;i++) {
			for(int j=1;j<=10;j++) {
				System.out.print(i+" x "+j+" = "+i*j+"  ");
			}System.out.println();
		}System.out.println();
	}

}
